import { useState, useEffect } from 'react'
import { Document, Page, pdfjs } from 'react-pdf'
import resume from '../../resume.pdf'
import "react-pdf/dist/esm/Page/AnnotationLayer.css";
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;


export const Resume = () => {
    const [pageNumber, setPageNumber] = useState(1);
    const [numPages, setNumPages] = useState(null);
    const [width, setWidth] = useState(1200);


    useEffect(() => {
        setWidth(window.innerWidth);
      }, []);
 function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  return (
    <>
        <Document file={resume} onLoadSuccess={onDocumentLoadSuccess}  options={{ workerSrc: "/pdf.worker.js" }} className="flex justify-center relative top-28">
            <Page pageNumber={pageNumber} scale={width > 786 ? 1.7 : 0.6} debug={true}/>
            <Page pageNumber={pageNumber + 1} scale={width > 786 ? 1.7 : 0.6} />
        </Document>
        <p className='text-white'>
        Page {pageNumber} of {numPages}
      </p> 
     </> 
  )
}
